        this.postalcode = 0;
        this.distance = 25;
        this.items = [];
    }
    ngOnInit() {
    }
    zipCodeChanged(e) {
        this.postalcode = e.target.value;
    }
    distanceChanged(e) {
        this.distance = e.target.value;
    }
