            },
            body: JSON.stringify({
                data: {
                    'filterRadius': {
                        'postalcode': this.postalcode,
                        'miles': this.distance
                    }
                }
            })
        });
        const result = await response.json();
        console.log(result);
        this.items = result.data;
    }
}
