import * as i0 from "@angular/core";
export class ApiServiceService {
    constructor() {
        this.items = [];
    }
    async search(postalcode, distance) {
        const response = await fetch('https://api.rescuegroups.org/v5/public/orgs/search/?limit=10&page=2&sort=distance', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/vnd.api+json',
                'Authorization': 'yQf20ay8'
            },
            body: JSON.stringify({
                data: {
                    'filterRadius': {
                        'postalcode': postalcode,
                        'miles': distance
                    }
                }
            })
        });
        const result = await response.json();
        this.items = result.data;
        console.log(this.items);
        this.sortArray(this.items);
        console.log(this.items);
    }
    sortArray(array) {
        let newArray = [];
        let smallestItem = array[0];
        do {
            for (let item of array) {
                if (item.attributes.distance < smallestItem.attributes.distance) {
                    smallestItem = item;
                }
            }
            newArray.push(smallestItem);
            array.splice(array.indexOf(smallestItem), 1);
        } while (array.length > 0);
        if (array.length == 0) {
            array = newArray;
        }
    }
}
