import * as i0 from "@angular/core";
export class ApiServiceService {
    constructor() {
        this.items = [];
        this.filters = [
            {
                "fieldName": "statuses.name",
                "operation": "equals",
                "criteria": "Available"
            },
            {
                "fieldName": "species.singular",
                "operation": "equals",
                "criteria": "Cat"
            },
            {
                "fieldName": "species.singular",
                "operation": "equals",
                "criteria": "Dog"
            },
            {
                "fieldName": "animals.ageGroup",
                "operation": "equals",
                "criteria": "Baby"
            },
            {
                "fieldName": "animals.ageGroup",
                "operation": "equals",
                "criteria": "Young"
            },
            {
                "fieldName": "animals.ageGroup",
                "operation": "equals",
                "criteria": "Adult"
            },
            {
                "fieldName": "animals.ageGroup",
                "operation": "equals",
                "criteria": "Senior"
            },
            {
                "fieldName": "animals.coatLength",
                "operation": "equals",
                "criteria": "Short"
            },
            {
                "fieldName": "animals.coatLength",
                "operation": "equals",
                "criteria": "Medium"
            },
            {
                "fieldName": "animals.coatLength",
                "operation": "equals",
                "criteria": "Long"
            },
            {
                "fieldName": "animals.sex",
                "operation": "equals",
                "criteria": "Male"
            },
            {
                "fieldName": "animals.sex",
                "operation": "equals",
                "criteria": "Female"
            },
            {
                "fieldName": "animals.isKidsOk",
                "operation": "equals",
                "criteria": "true"
            },
            {
                "fieldName": "animals.isDogsOk",
                "operation": "equals",
                "criteria": "true"
            },
            {
                "fieldName": "animals.isCatsOk",
                "operation": "equals",
                "criteria": "true"
            },
            {
                "fieldName": "animals.sizeGroup",
                "operation": "equals",
                "criteria": "Small"
            },
            {
                "fieldName": "animals.sizeGroup",
                "operation": "equals",
                "criteria": "Medium"
            },
            {
                "fieldName": "animals.sizeGroup",
                "operation": "equals",
                "criteria": "Large"
            }
        ];
    }
    async searchOrgs(postalcode, distance, page) {
        const response = await fetch('https://api.rescuegroups.org/v5/public/orgs/search/?limit=10&page=' + page + '&sort=orgs.distance', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/vnd.api+json',
                'Authorization': 'yQf20ay8'
            },
            body: JSON.stringify({
                data: {
                    'filterRadius': {
                        'postalcode': postalcode,
                        'miles': distance
                    }
                }
            })
        });
        const result = await response.json();
        if (result.data != undefined) {
            this.items = result.data;
        }
        console.log(this.items);
    }
    async searchAnimals(postalcode, distance, page) {
        const response = await fetch('https://api.rescuegroups.org/v5/public/animals/search/?limit=10&page=' + page + '&sort=animals.distance', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/vnd.api+json',
                'Authorization': 'yQf20ay8'
            },
            body: JSON.stringify({
                data: {
                    'filterRadius': {
                        'postalcode': postalcode,
                        'miles': distance
                    },
                    'filters': this.filters
                }
            })
        });
        const result = await response.json();
        if (result.data != undefined) {
            this.items = result.data;
        }
        console.log(this.items);
    }
}
