import * as i0 from "@angular/core";
export class ApiServiceService {
    constructor() {
        this.criteria = {
            ageGroup: [],
            coatLength: [],
            gender: [],
            sizeGroup: []
        };
        this.criteriaTemplate = {
            ageGroup: ["Baby", "Young", "Adult", "Senior"],
            coatLength: ["Short", "Medium", "Long"],
            gender: ["Male", "Female"],
            sizeGroup: ["Small", "Medium", "Large"]
        };
        this.items = [];
        this.filters = [
            {
                "fieldName": "statuses.name",
                "operation": "equals",
                "criteria": "Available"
            },
            {
                "fieldName": "animals.ageGroup",
                "operation": "equals",
                "criteria": ["Baby", "Young", "Adult", "Senior"]
            },
            {
                "fieldName": "animals.coatLength",
                "operation": "equals",
                "criteria": ["Short", "Medium", "Long"]
            },
            {
                "fieldName": "animals.sex",
                "operation": "equals",
                "criteria": ["Male", "Female"]
            },
            {
                "fieldName": "animals.isKidsOk",
                "operation": "equals",
                "criteria": "true"
            },
            {
                "fieldName": "animals.isDogsOk",
                "operation": "equals",
                "criteria": "true"
            },
            {
                "fieldName": "animals.isCatsOk",
                "operation": "equals",
                "criteria": "true"
            },
            {
                "fieldName": "animals.sizeGroup",
                "operation": "equals",
                "criteria": ["Small", "Medium", "Large", "X-Large"]
            }
        ];
        this.filterProcess = "1";
    }
    async searchOrgs(postalcode, distance, page) {
        const response = await fetch('https://api.rescuegroups.org/v5/public/orgs/search/?limit=10&page=' + page + '&sort=orgs.distance', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/vnd.api+json',
                'Authorization': 'yQf20ay8'
            },
            body: JSON.stringify({
                data: {
                    'filterRadius': {
                        'postalcode': postalcode,
                        'miles': distance
                    }
                }
            })
        });
        const result = await response.json();
        if (result.data != undefined) {
            this.items = result.data;
        }
        console.log(this.items);
    }
    async searchAnimals(postalcode, distance, page) {
        const response = await fetch('https://api.rescuegroups.org/v5/public/animals/search/?limit=10&page=' + page + '&sort=animals.distance', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/vnd.api+json',
                'Authorization': 'yQf20ay8'
            },
            body: JSON.stringify({
                data: {
                    'filterRadius': {
                        'postalcode': postalcode,
                        'miles': distance
                    },
                    'filters': this.filters,
                    "filterProcessing": "1 AND (2 OR 3) AND 4 AND 5 AND 6 AND 7 AND 8 AND 9 AND 10"
                }
            })
        });
        const result = await response.json();
        if (result.data != undefined) {
            this.items = result.data;
        }
        console.log(this.items);
    }
    getFilterData(data) {
        this.filterOptions = data;
        this.applyFilters();
    }
    applyFilters() {
        if (this.filterOptions.breed[0] == 1) {
            this.filters.push({
                "fieldName": "species.singular",
                "operation": "equals",
                "criteria": "Dog"
            });
        }
        else if (this.filterOptions.breed[0] == 0) { }
        if (this.filterOptions.breed[1] == 1) {
            this.filters.push({
                "fieldName": "species.singular",
                "operation": "equals",
                "criteria": "Cat"
            });
        }
        else if (this.filterOptions.breed[1] == 0) { }
        for (let i = 0; i < this.filterOptions.ageGroup.length; i++) {
            if (this.filterOptions.ageGroup[i] == 1) {
                this.criteria.ageGroup.push(this.criteriaTemplate.ageGroup[i]);
            }
            else { }
        }
        for (let i = 0; i < this.filterOptions.coatLength.length; i++) {
            if (this.filterOptions.coatLength[i] == 1) {
                this.criteria.coatLength.push(this.criteriaTemplate.coatLength[i]);
            }
            else { }
        }
        for (let i = 0; i < this.filterOptions.gender.length; i++) {
            if (this.filterOptions.gender[i] == 1) {
                this.criteria.gender.push(this.criteriaTemplate.gender[i]);
            }
            else { }
        }
        this.updateFilterProcess();
    }
    updateFilterProcess() {
    }
}
