import * as i0 from "@angular/core";
export class ApiServiceService {
    constructor() {
        this.items = [];
    }
    async search(postalcode, distance) {
        const response = await fetch('https://api.rescuegroups.org/v5/public/orgs/search', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/vnd.api+json',
                'Authorization': 'yQf20ay8'
            },
            body: JSON.stringify({
                data: {
                    'filterRadius': {
                        'postalcode': postalcode,
                        'miles': distance
                    }
                }
            })
        });
        const result = await response.json();
        this.items = result.data;
        console.log(this.items);
        this.sortArray(this.items);
        console.log(this.items);
    }
    sortArray(array) {
        let newArray = [];
        let largestItemIndex = 0;
        do {
            console.log("before: " + largestItemIndex);
            console.log("before length : " + array.length);
            for (let i = 0; i < array.length; i++) {
                if (array[i].attributes.distance > array[largestItemIndex].attributes.distance) {
                    largestItemIndex = i;
                    console.log("index: " + largestItemIndex);
                    console.log("after: " + largestItemIndex);
                }
            }
            newArray.unshift(array[largestItemIndex]);
            array.splice(largestItemIndex, 1);
        } while (array.length > 0);
        if (array.length == 0) {
            array = newArray;
        }
    }
}
